	function __construct($stringlist="",$delimeter=" ",$language="english"){
		$this->stringlist=$stringlist;
		$this->delimeter=$delimeter;
		$this->language=$language;
		$newlist=array();
		
		if (in_array($language, $this->language_meta)) {
			eval('$lanselect=$this->'.$language.';');


		}else{
			$this->language="english";
			$lanselect=$this->english;;
			$this->error="1";
			$this->errormsg="language not supported. defaulted to default language: ".$this->lanselect;
		}
		$data=explode($delimeter, $stringlist);
		$res=array();
		
		for ($i=0; $i < count($data); $i++) {
			$s=trim($data[$i]);
			if (!stopwords::in_arrayi($s, $lanselect)) {
				array_push($res, $s);
				
			}else{
				//
			}
		

	}
	$newlist=$res;
	$this->results=implode(' ', $newlist);
	
}

static function in_arrayi($needle, $haystack) {
    return in_array(strtolower($needle), array_map('strtolower', $haystack));
